INSERT INTO `repository_config` VALUES (1,'SqlServer','SELECT name schemaname\r\nFROM sys.databases \r\nWHERE database_id > 4 ','use [#{schemaName}]\r\n\r\nSELECT \r\n    DB_NAME() AS [schemaname],\r\n   SCHEMA_NAME(t.schema_id)+\'.\'+ t.name AS [tablename],\r\n    isnull(ep.value,\'\') AS remarks,\r\n    CASE WHEN EXISTS (\r\n               SELECT *\r\n               FROM sys.indexes i\r\n               WHERE i.object_id = t.object_id\r\n                 AND i.is_primary_key = 1\r\n             ) THEN 0\r\n             ELSE 1\r\n       END AS [notkey]\r\nFROM \r\n    sys.tables t\r\nLEFT OUTER JOIN \r\n   sys.extended_properties ep ON t.object_id = ep.major_id AND ep.minor_id = 0\r\nWHERE \r\n    t.type_desc = \'USER_TABLE\' AND\r\n    t.is_ms_shipped = 0\r\nORDER BY \r\n    2',' use [#{schemaName}]\r\nSELECT \r\n    DB_NAME() AS databasename,\r\n	SCHEMA_NAME(t.schema_id) schemaname,\r\n    t.name AS tablename,\r\n    c.name AS columnname,\r\n     TYPE_NAME(c.system_type_id)  AS datatype,\r\n    c.max_length AS length,\r\n    c.is_nullable AS nullable,\r\n    SCHEMA_NAME(t.schema_id) AS dbo_name,\r\n    ISNULL(ep.value, \'\') AS remark,\r\n		CASE \r\n        WHEN pk.COLUMN_NAME IS NOT NULL THEN 1\r\n        ELSE 0\r\n    END AS primarykey\r\nFROM \r\n    sys.tables t\r\nINNER JOIN \r\n    sys.columns c ON t.object_id = c.object_id\r\nLEFT JOIN \r\n    sys.extended_properties ep on t.object_id=ep.major_id and c.column_id=ep.minor_id and ep.name=\'MS_Description\'\r\nLEFT JOIN \r\n    (\r\n        SELECT tc.TABLE_NAME, kcu.COLUMN_NAME\r\n        FROM INFORMATION_SCHEMA.TABLE_CONSTRAINTS as tc\r\n        INNER JOIN INFORMATION_SCHEMA.KEY_COLUMN_USAGE as kcu ON tc.CONSTRAINT_NAME = kcu.CONSTRAINT_NAME\r\n        WHERE tc.table_schema +\'.\' + tc.TABLE_NAME = \'#{tableName}\' AND tc.CONSTRAINT_TYPE = \'PRIMARY KEY\'\r\n    ) pk ON t.name = pk.TABLE_NAME AND c.name = pk.COLUMN_NAME\r\nWHERE \r\n    t.type = \'U\'\r\n	and SCHEMA_NAME(t.schema_id)+\'.\'+ t.name =\'#{tableName}\'\r\nORDER BY \r\n    t.name, c.column_id','use [#{schemaName}]\r\nSELECT \r\n    t.name AS tablename,\r\n    c.name AS columnname,\r\n    SCHEMA_NAME(t.schema_id) as schemaname\r\nFROM \r\n    sys.tables t\r\nINNER JOIN \r\n    sys.columns c ON t.object_id = c.object_id\r\nINNER JOIN \r\n    sys.index_columns ic ON ic.object_id = c.object_id AND ic.column_id = c.column_id\r\nINNER JOIN \r\n    sys.indexes i ON i.object_id = ic.object_id AND i.index_id = ic.index_id\r\nWHERE \r\n    t.type = \'U\' AND i.is_primary_key = 1\r\n    AND SCHEMA_NAME(t.schema_id)+\'.\'+ t.name =\'#{tableName}\'\r\nORDER BY \r\n    t.name, c.column_id',0,NULL,NULL,NULL,NULL,'com.microsoft.sqlserver.jdbc.SQLServerDriver','jdbc:sqlserver://${host}:${port};databaseName=${repositoryName}',1433,'[',']','SELECT @@VERSION;',NULL,NULL),(2,'MySql','SELECT distinct TABLE_SCHEMA schemaname\r\nFROM INFORMATION_SCHEMA.TABLES\r\norder by 1','SELECT TABLE_SCHEMA schemaname, TABLE_NAME tablename,TABLE_COMMENT remarks,\r\nCASE WHEN EXISTS (\r\n               SELECT *\r\n               FROM INFORMATION_SCHEMA.KEY_COLUMN_USAGE\r\n               WHERE TABLE_SCHEMA = t.TABLE_SCHEMA\r\n                 AND TABLE_NAME = t.TABLE_NAME\r\n                 AND CONSTRAINT_NAME = \'PRIMARY\'\r\n             ) THEN 0\r\n             ELSE 1\r\n       END AS notkey\r\nFROM INFORMATION_SCHEMA.TABLES t\r\nWHERE TABLE_SCHEMA = \'#{schemaName}\' and t.TABLE_TYPE = \'BASE TABLE\'  order by 2','SELECT \r\nTABLE_SCHEMA schemaname,\r\nTABLE_NAME tablename, \r\nCOLUMN_NAME columnname,\r\n COLUMN_COMMENT remark,\r\n DATA_TYPE datatype,\r\n CHARACTER_MAXIMUM_LENGTH AS length,\r\n IS_NULLABLE nullable\r\nFROM INFORMATION_SCHEMA.COLUMNS\r\nWHERE 1=1\r\nAND TABLE_SCHEMA = \'#{schemaName}\'\r\nAND TABLE_NAME = \'#{tableName}\'\r\n\r\n','SELECT TABLE_SCHEMA schemaname,TABLE_NAME tablename, COLUMN_NAME columnname\r\nFROM INFORMATION_SCHEMA.KEY_COLUMN_USAGE\r\nWHERE CONSTRAINT_NAME = \'PRIMARY\'\r\nAND TABLE_SCHEMA = \'#{schemaName}\'\r\nAND TABLE_NAME = \'#{tableName}\'',0,NULL,NULL,NULL,NULL,'com.mysql.cj.jdbc.Driver','jdbc:mysql://${host}:${port}/${repositoryName}',3306,'`','`','SELECT VERSION();',NULL,NULL),(3,'Oracle','SELECT U.username schema_name , u.created create_time FROM ALL_USERS U WHERE (U.USERNAME IS NOT NULL) \r\norder by 1','SELECT  OWNER schema_name, TABLE_NAME ,T.COMMENTS  TABLE_COMMENT\r\nFROM ALL_TAB_COMMENTS T WHERE T.TABLE_TYPE =\'TABLE\' AND   OWNER = \'#{schemaName}\' \r\n\r\norder by 2','SELECT\r\n    U.OWNER schema_name,\r\n    U.TABLE_NAME,\r\n    U.COLUMN_ID,\r\n    U.COLUMN_NAME ,\r\n    C.COMMENTS AS COLUMN_COMMENT,\r\n    U.DATA_TYPE ,\r\n    CASE WHEN U.DATA_TYPE = \'NUMBER\' THEN NVL(U.DATA_PRECISION,38)||\',\'||NVL(U.DATA_SCALE,0)\r\nWHEN CHAR_LENGTH = 0 THEN NULL \r\n   ELSE TO_CHAR(CHAR_LENGTH) END  AS DATA_LENGTH,\r\n    U.DATA_PRECISION,\r\n    U.DATA_SCALE,\r\n    decode(U.NULLABLE,\'Y\',1,0) AS \"NULLABLE\",\r\n    CASE WHEN K.CONSTRAINT_TYPE = \'P\' THEN 1 ELSE 0 END AS IS_primary_key,\r\n    U.DATA_DEFAULT default_value\r\nFROM\r\n    DBA_TAB_COLUMNS U\r\n    LEFT JOIN DBA_COL_COMMENTS C ON U.TABLE_NAME = C.TABLE_NAME AND U.COLUMN_NAME = C.COLUMN_NAME AND U.OWNER = C.OWNER\r\n    LEFT JOIN (\r\n        SELECT\r\n            A.OWNER,\r\n            A.TABLE_NAME,\r\n            A.COLUMN_NAME,\r\n            A.POSITION,\r\n            B.CONSTRAINT_TYPE\r\n        FROM\r\n            DBA_CONS_COLUMNS A,\r\n            DBA_CONSTRAINTS B\r\n        WHERE A.OWNER = \'#{schemaName}\'  AND A.TABLE_NAME = \'#{tableName}\'\r\n           AND A.CONSTRAINT_NAME = B.CONSTRAINT_NAME\r\n            AND B.CONSTRAINT_TYPE = \'P\' \r\n                  AND A.OWNER = B.OWNER\r\n    ) K ON U.OWNER = K.OWNER AND U.TABLE_NAME = K.TABLE_NAME \r\n    AND U.COLUMN_NAME = K.COLUMN_NAME \r\nWHERE\r\n    U.OWNER = \'#{schemaName}\'\r\n    AND U.TABLE_NAME = \'#{tableName}\'\r\n  ORDER BY U.COLUMN_ID','    SELECT\r\nA.OWNER \"schemaname\",\r\n    B.TABLE_NAME \"tablename\",\r\n    B.COLUMN_NAME \"columnname\"\r\nFROM\r\n  DBA_TABLES A\r\n  LEFT JOIN DBA_CONS_COLUMNS  B ON A.OWNER=B.OWNER AND A.TABLE_NAME=B.TABLE_NAME\r\nINNER JOIN DBA_CONSTRAINTS C ON B.owner=C.owner AND B.table_name=C.table_name \r\n    AND B.constraint_name=C.constraint_name AND C.CONSTRAINT_TYPE=\'P\'\r\nWHERE\r\n \r\n    A.OWNER = \'#{schemaName}\'\r\n AND    A.TABLE_NAME = \'#{tableName}\'',0,NULL,NULL,NULL,NULL,'oracle.jdbc.driver.OracleDriver','jdbc:oracle:thin:@${host}:${port}/${repositoryName}',1521,'\"','\"','SELECT * FROM v$version',' SELECT \r\n a.TABLE_OWNER schema_name,\r\n a.TABLE_NAME,\r\n    a.INDEX_NAME,\r\n    decode(a.UNIQUENESS,\'UNIQUE\',1,0) is_unique,\r\n    b.COLUMN_NAME,\r\n    b.COLUMN_POSITION,\r\n    decode(b.DESCEND,\'ASC\',1,0) is_asc\r\nFROM \r\n    ALL_INDEXES a\r\nJOIN \r\n    ALL_IND_COLUMNS b\r\nON \r\n    a.INDEX_NAME = b.INDEX_NAME\r\nWHERE  \r\n    a.TABLE_NAME = \'#{tableName}\'\r\n    AND a.TABLE_OWNER = \'#{schemaName}\'\r\nORDER BY \r\n    a.INDEX_NAME, b.COLUMN_POSITION',NULL),(4,'PostgreSql','SELECT nspname AS schemaname, NULL AS TABLE_CATALOG FROM pg_catalog.pg_namespace \r\n          WHERE nspname <> \'pg_toast\' AND (nspname !~ \'^pg_temp_\' \r\n          OR nspname = (pg_catalog.current_schemas(true))[1]) AND (nspname !~ \'^pg_toast_temp_\' \r\n          OR nspname = replace((pg_catalog.current_schemas(true))[1], \'pg_temp_\', \'pg_toast_temp_\')) order by 1','SELECT  n.nspname AS schemaname, c.relname AS tablename, \r\n               CASE n.nspname ~ \'^pg_\' OR n.nspname = \'information_schema\' \r\n               WHEN true THEN CASE \r\n               WHEN n.nspname = \'pg_catalog\' OR n.nspname = \'information_schema\' THEN CASE c.relkind \r\n                WHEN \'r\' THEN \'SYSTEM TABLE\' \r\n                WHEN \'v\' THEN \'SYSTEM VIEW\' \r\n                WHEN \'i\' THEN \'SYSTEM INDEX\' \r\n                ELSE NULL \r\n                END \r\n               WHEN n.nspname = \'pg_toast\' THEN CASE c.relkind \r\n       