INSERT INTO `server_command` VALUES (35,'1','(echo INFO ALL;echo exit)|#{oggPath}/ggsci|grep -E \"EXTRACT|REPLICAT\" |awk \'BEGIN {FS=\" +\"} {print $1\",\"$3\",\"$2\",\"$4\",\"$5}\'','02',NULL,NULL,NULL,NULL,0,'查看ogg进程列表'),(36,'2','(echo info #{processName};echo exit)|#{oggPath}/ggsci|grep -E -i \"status|状态|Checkpoint|检查点|Process|进程|Log|日志|Record|记录|RBA|LRI\"|awk \'BEGIN {FS=\" +\"} {print $1\",\"$2\",\"$3\",\"$4\",\"$5\",\"$6\",\"$7\",\"$8}\'','02',NULL,NULL,NULL,NULL,0,'查看ogg进程信息'),(37,'1','cmd /c for /f \"tokens=1,3,2,4,5\" %a in (\'echo info all ^|#{oggPath}\\ggsci\') do @( \n if \"%a\"==\"EXTRACT\" (echo %a,%c,%b,%d,%e ) ELSE \n if \"%a\"==\"REPLICAT\" (echo %a,%c,%b,%d,%e)\n  )','01',NULL,NULL,NULL,NULL,0,'查看ogg进程列表'),(38,'6','cmd /c type #{oggPath}\\dirprm\\#{processName}.prm','01',NULL,NULL,NULL,NULL,0,'查看进程参数'),(39,'6','cat #{oggPath}/dirprm/#{processName}.prm','02',NULL,NULL,NULL,NULL,0,'查看进程参数'),(40,'4','(echo start #{processName} ;echo exit)|#{oggPath}/ggsci','02',NULL,NULL,NULL,NULL,0,'启动进程'),(41,'4','cmd /c for /f \"tokens=1-8\" %a in (\'echo start #{processName}^|#{oggPath}\\ggsci^|findstr /r /v /c:\"^$\"\') do @( echo %a,%b,%c,%d,%e,%f,%g,%h)','01',NULL,NULL,NULL,NULL,0,'启动进程'),(42,'5','cmd /c for /f \"tokens=1-8\" %a in (\'echo stop #{processName}^|#{oggPath}\\ggsci^|findstr /r /v /c:\"^$\"\') do @( echo %a,%b,%c,%d,%e,%f,%g,%h)','01',NULL,NULL,NULL,NULL,0,'停止进程'),(43,'5','(echo stop #{processName} ;echo exit)|#{oggPath}/ggsci','02',NULL,NULL,NULL,NULL,0,'停止进程'),(44,'7','(echo delete #{processName} ;echo exit)|#{oggPath}/ggsci','02',NULL,NULL,NULL,NULL,0,'删除进程'),(45,'7','cmd /c for /f \"tokens=1-8\" %a in (\'echo delete #{processName}^|#{oggPath}\\ggsci^|findstr /r /v /c:\"^$\"\') do @( echo %a,%b,%c,%d,%e,%f,%g,%h)','01',NULL,NULL,NULL,NULL,0,'删除进程'),(46,'3','cmd /c for /f \"tokens=1-6\" %a in (\'echo stats #{processName} total^|#{oggPath}\\ggsci^|findstr /i \"Replicating Extracting Total\"\') do @( if \"%a\"==\"Total\" (echo %a,%b, ,%c,%d,%e,%f ) ELSE ( echo %a,%b,%c,%d,%e,%f ))','01',NULL,NULL,NULL,NULL,0,'查看ogg进程详情'),(47,'3','export LANG=en_US.UTF-8;(echo stats #{processName} total;echo exit)|#{oggPath}/ggsci|grep -E -i \"自|从|总数|Replicating from|Extracting from|Total statistics since|Total inserts|Total updates|Total deletes|Total discards|Total operations\"|awk \'BEGIN {FS=\" +\"} {print $1\",\"$2\",\"$3\",\"$4\",\"$5\",\"$6}\'','02',NULL,NULL,NULL,NULL,0,'查看ogg进程详情'),(48,'2','cmd /c for /f \"tokens=1-8\" %a in (\'echo info #{processName}^|#{oggPath}\\ggsci^|findstr /i \"EXTRACT REPLICAT CHECKPOINT PROCESS READ Log Record LSN RBA\"\') do @( echo %a,%b,%c,%d,%e,%f,%g,%h)','01',NULL,NULL,NULL,NULL,0,'查看ogg进程信息'),(49,'8','cmd /c type #{oggPath}\\dirrpt\\#{processName}.rpt','01',NULL,NULL,NULL,NULL,0,'查看进程日志'),(50,'8','cat  #{oggPath}/dirrpt/#{processName}.rpt | iconv -f GB18030 -t utf-8','02',NULL,NULL,NULL,NULL,0,'查看进程日志'),(51,'9','(echo exit)|#{oggPath}/ggsci| grep -v -e \'^\\s*$\'|awk \'BEGIN {FS=\" +\"} {print $1\",\"$2\",\"$3\",\"$4\",\"$5\",\"$6\",\"$7\",\"$8}\'','02',NULL,NULL,NULL,NULL,0,'查看ogg版本信息'),(52,'9','cmd /c for /f \"tokens=1-8\" %a in (\'echo exit^|#{oggPath}\\ggsci^|findstr /r /v /c:\"^$\"\') do @( echo %a,%b,%c,%d,%e,%f,%g,%h)','01',NULL,NULL,NULL,NULL,0,'查看ogg版本信息'),(53,'10','#{\r\n    if(sourceRepo.dataSourceName == \'MYSQL\'){\r\n        return \'su - mysql\';\r\n    }\r\n    if(sourceRepo.dataSourceName == \'Oracle\'){\r\n        return \'su - oracle\';\r\n    }\r\n}\r\necho 切换到 OGG 安装路径下的 bin 目录\r\ncd #{sourceServer.oggPath}\r\n\r\necho 创建进程目录\r\nmkdir #{sourceServer.oggPath}/dirdat/#{processName}\r\n\r\necho 进入ogg\r\n./ggsci\r\n\r\n--执行 dblogin 命令登录到数据库实例\r\ndblogin #{ \r\n    if(sourceRepo.dataSourceName == \'MYSQL\'){\r\n        return \'sourcedb \' + sourceRepo.repositoryName + \'@\' + sourceRepo.host + \':\' + sourceRepo.port;\r\n    }elsif(sourceRepo.dataSourceName != \'Oracle\'){\r\n        return \'sourcedb \' + sourceRepo.repositoryName;\r\n    }else{\r\n    	return nil;\r\n    }\r\n} userid #{sourceRepo.username}, password #{sourceRepo.password} \r\n\r\n--创建子目录\r\n#{\r\n	if(createSubdirs){\r\n		return \'create subdirs\';\r\n	}\r\n}\r\n\r\n--添加表附加日志\r\n#{\r\n	if(addTranData){\r\n		let str = \'\';\r\n		let allColstr = \'\';\r\n		if(allCol){\r\n			allColstr = \'allcols\';\r\n		}\r\n		for x in tableList {\r\n		  str =  str + \'ADD TRANDATA \' +   sourceDbSchema + \'.\' + x + \' \' + allColstr + \'\\r\\n\';\r\n		}\r\n		return str;\r\n	}\r\n}\r\n\r\n--添加名为  的抽取线程，读取事务日志，开始时间为当前时间\r\nadd extract #{extName},tranlog,begin now\r\n\r\n--添加名为  的数据文件，属于  这个抽取线程\r\nadd exttrail #{sourceTrailPath}, extract #{extName},MEGABYTES  #{megaBytes}\r\n\r\n--添加名为   的投递进程,指定投递的trail文件\r\nADD  EXTRACT  #{pumName},EXTTRAILSOURCE  #{sourceTrailPath},EXTSEQNO #{extSeqNo},EXTRBA #{extRba}\r\n\r\n--添加名为  的目标端数据文件，属于  这个投递线程，单个文件大小\r\nADD  RMTTRAIL  #{targetTrailPath},EXTRACT  #{pumName},MEGABYTES  #{megaBytes}\r\n\r\n#{\r\n	let str = \'\';\r\n	if(isInit){\r\n		str = str + \'--添加源端初始化进程\' + \'\\r\\n\';\r\n		str = str + \'ADD EXTRACT \' + einName + \',sourceistable\';\r\n	}\r\n	return str;\r\n}\r\n         \r\n--退出ggsci\r\nexit\r\n\r\n--退出命令行\r\nexit\r\n','02',NULL,NULL,NULL,NULL,0,'源端执行命令'),(54,'11','-- 切换到 OGG 安装路径下的 bin 目录\r\ncd #{targetServer.oggPath}\r\n\r\n--进入ogg\r\n./ggsci\r\n\r\n--执行 dblogin 命令登录到数据库实例\r\ndblogin userid #{targetRepo.username}, password #{targetRepo.password} \r\n\r\n#{\r\n	if(addCheckPointTable){\r\n		\'add checkpointtable \' +  checkPointTable\r\n	}\r\n}\r\n\r\n--添加目标复制进程\r\nADD  REPLICAT  #{repName},EXTTRAIL  #{targetTrailPath},EXTSEQNO #{extSeqNo},EXTRBA #{extRba},checkpointtable #{checkPointTable}\r\n\r\n#{\r\n	let str = \'\';\r\n	if(!isInit){\r\n		return nil;\r\n	}\r\n	str = str + \'--添加目标端初始化进程\' + \'\\r\\n\';\r\n	if(isRange){\r\n		for i in range(0, rinRange){\r\n			str = str + \'ADD REPLICAT \' + rinName + i +\',exttrail \' + targetRinTrailPath + \',EXTSEQNO 0,EXTRBA 0,checkpointtable \' + checkPointTable + \';\\r\\n\';		\r\n		}\r\n	}else{\r\n		str = str + \'ADD REPLICAT \' + rinName + \',exttrail \' + targetRinTrailPath + \',EXTSEQNO 0,EXTRBA 0,checkpointtable \' + checkPointTable + \';\';\r\n	}\r\n	return str;\r\n}\r\n            \r\n--退出ggsci\r\nexit\r\n\r\n--退出命令行\r\nexit','02',NULL,NULL,NULL,NULL,0,'目标端执行命令'),(55,'12','-- 定义抽取进程名称\r\nEXTRACT #{extName}\r\n#{\r\n    if(sourceRepo.dataSourceName == \'MYSQL\'){\r\n        let str = \"\";\r\n        \r\n            str = str +  \"setenv (MYSQL_HOME=\\\"\" + mysqlHome + \"\\\")\"  + \"\\r\\n\";\r\n        \r\n        \r\n             str = str +  \"tranlogoptions altlogdest \" + tranlogOptions   + \"\\r\\n\";\r\n        \r\n        \r\n              str = str +  \"dboptions \" + dbOptions;\r\n        \r\n        return str;\r\n    }\r\n}\r\n\r\n#{\r\n    if(sourceRepo.dataSourceName == \'Oracle\'){\r\n        let str = \"\";\r\n        str = str +  \"setenv (ORACLE_SID=\" + sourceRepo.repositoryName + \")\"  + \"\\r\\n\";\r\n        str = str +  \"setenv (NLS_LANG=\" + sourceNlsLang + \")\";\r\n        return str;\r\n    }\r\n}\r\n\r\n-- 指定源数据库的名称。GoldenGate 连接到源数据库的用户名和密码。\r\n#{ \r\n    if(sourceRepo.dataSourceName == \'MYSQL\'){\r\n        return \'sourcedb \' + sourceRepo.repositoryName + \'@\' + sourceRepo.host + \':\' + sourceRepo.port;\r\n    }elsif(sourceRepo.dataSourceName != \'Oracle\'){\r\n        return \'sourcedb \' + sourceRepo.repositoryName;\r\n    }else{\r\n    	return nil;\r\n    }\r\n} USERID #{sourceRepo.username} PASSWORD #{sourceRepo.password} \r\n-- 定义抽取进程使用的文件路径和格式\r\nEXTTRAIL #{sourceTrailPath}#{\r\n    if(format){\r\n        return \',format release \' + formatRelease;\r\n    }\r\n}\r\n-- 指定当发生数据丢失时记录丢失事件的文件路径和名称前缀。在这个例子中，(\"./dirrpt/xxx.DSC\") 指定了一个文件路径和名称前缀，GoldenGate 将事件追加到此文件中，\"APPEND\"表示在文件的末尾添加新事件，而不是覆盖之前的事件。\"MEGABYTES 300\" 指定 GoldenGate 可以使用的最大磁盘空间量 (300MB)。\r\nDISCARDFILE ./dirrpt/#{extName}.DSC,APPEND,MEGABYTES 300 \r\n-- 启用 GoldenGate 能够处理的源表的动态调整。 \r\nDYNAMICRESOLUTION \r\n-- 指示 GoldenGate 是否应该解释源表中截断操作的 DDL 语句。\r\nGETTRUNCATES \r\n-- 指定 GoldenGate 是否应捕获更新操作之前的行。\r\nGETUPDATEBEFORES \r\n-- 指示不使用压缩算法来压缩删除操作的数据，这将创建这些数据的完整副本，可能会导致文件变得更大。\r\nNOCOMPRESSDELETES \r\n-- 指示不使用压缩算法来压缩更新操作的数据，这将创建这些数据的完整副本，可能会导致文件变得更大。  \r\nNOCOMPRESSUPDATES \r\n\r\n#{\r\n	let str = \'\';\r\n	for x in tableList {\r\n	  str =  str + \'TABLE \' +   sourceDbSchema + \'.\' + x + \'; \\r\\n \';\r\n	}\r\n	return str;\r\n}','02',NULL,NULL,NULL,NULL,0,'抽取进程参数'),(56,'13','-- 定义投递进程参数\r\n-- 定义抓取进程实例名 \r\nEXTRACT #{pumName}  \r\n-- 远程主机IP，管理端口号为7809\r\nRMTHOST #{targetServer.serverIp}, MGRPORT 7809, COMPRESS, TCPBUFSIZE 10485760, TCPFLUSHBYTES 10485760\r\n\r\n--GoldenGate写入的日志数据存储位置为./dirdat/ts，并指定日志文件格式版本为12.3\r\nRMTTRAIL #{targetTrailPath}\r\n\r\n--开启动态冲突解决功能，直接覆盖原记录\r\nDYNAMICRESOLUTION\r\n\r\n--配置为源端不做任何转换，只传输原始数据\r\nPASSTHRU\r\n\r\n-- 指定GoldenGate写入的日志文件数量为5000\r\nNUMFILES 5000  \r\n\r\n#{\r\n	let str = \'\';\r\n	for x in tableList {\r\n	  str =  str + \'TABLE \' +   sourceDbSchema + \'.\' + x + \'; \\r\\n \';\r\n	}\r\n	return str;\r\n}','02',NULL,NULL,NULL,NULL,0,'投递进程参数'),(57,'14','-- 配置 Replicat 进程名为 REPHIS\r\nREPLICAT #{repName}\r\n-- 设置环境变量 SERVICE_NAME 为 ods\r\nSETENV(SERVICE_NAME=#{targetRepo.repositoryName})\r\n-- 设置环境变量 NLS_LANG 为 AMERICAN_AMERICA.ZHS16GBK\r\nSETENV(NLS_LANG = #{targetNlsLang})\r\n-- 设置连接源端数据库的用户名和密码\r\nUSERID #{targetRepo.username}, PASSWORD #{targetRepo.password}\r\n-- 配置遇到错误时的处理方式为直接丢弃，不写入重做日志\r\nREPERROR DEFAULT, DISCARD\r\n-- 如果遇到无效字符，则替换为指定的字符\r\nREPLACEBADCHAR ESCAPE\r\n-- 应用程序将不会更新任何列中没有实际值变化的记录。\r\nAPPLYNOOPUPDATES\r\n-- 配置丢弃数据的日志文件的路径和名称\r\nDISCARDFILE ./dirrpt/#{repName}.DSC APPEND, MEGABYTES 1024\r\n-- 注释掉，表示不启用冲突处理功能\r\n--HANDLECOLLISIONS\r\n-- 获取变更日志时，包含受支支持事务日志截断的数据\r\nGETTRUNCATES\r\n-- 在目标端插入所有的记录，包括在源端删除的记录\r\nINSERTALLRECORDS\r\n-- 应用程序将不会更新任何列中没有实际值变化的记录。\r\nAPPLYNOOPUPDATES\r\n-- 允许在目标端使用相同名称的多个表\r\nALLOWDUPTARGETMAP\r\n-- 目标表不存在时，自动创建目标表和字段\r\nASSUMETARGETDEFS\r\n\r\n#{\r\n	let str = \"\";\r\n	let i = 0;\r\n	for x in mapParams {\r\n		if(x!=nil){\r\n			str =  str +   x.COLS + \"\\r\\n\";\r\n		}\r\n		i=i+1;\r\n	}\r\n	return str;\r\n}','02',NULL,NULL,NULL,NULL,0,'复制进程参数'),(58,'15','--生成目标端复制进程参数\r\nwith \r\ntb as (\r\n\r\n#{\r\n	let str = \"\";\r\n	let length = count(keyParams);\r\n	let i = 1;\r\n	\r\n	for x in keyParams {\r\n		if(length==i){\r\n			str =  str +   \"select \'\" + targetDbSchema + \"\' target_db,\'\" + x.schemaname + \"\' source_db,\'\" + x.tablename + \"\' source_table,\'\" + x.columnname + \"\' source_column from dual\";\r\n		}else{\r\n			str =  str +   \"select \'\" + targetDbSchema + \"\' target_db,\'\" + x.schemaname + \"\' source_db,\'\" + x.tablename + \"\' source_table,\'\" + x.columnname + \"\' source_column from dual \\r\\n union all \\r\\n\";\r\n		}\r\n		i=i+1;\r\n	}\r\n	return str;\r\n}\r\n),cte2 as\r\n(\r\n      select A.OWNER,A.TABLE_NAME , tb.source_db , tb.source_table  from DBA_TABLES a  ,\r\n      (select distinct tb.source_db,tb.target_db,tb.source_table from tb ) tb\r\n      where A.OWNER||\'.\'||A.TABLE_NAME  = upper(tb.target_db||\'.\'||tb.source_table)\r\n)\r\n,cte as\r\n(\r\n    SELECT \r\n      A.OWNER,A.TABLE_NAME,\r\n      listagg( tb.source_column || \'=:par_\' || tb.source_column,\' and \') within group(order by A.OWNER,A.TABLE_NAME) par\r\n    FROM cte2 A\r\n    LEFT JOIN DBA_CONS_COLUMNS  B ON A.OWNER=B.OWNER AND A.TABLE_NAME=B.TABLE_NAME\r\n    INNER JOIN DBA_CONSTRAINTS C ON B.owner=C.owner AND B.table_name=C.table_name \r\n    inner join tb  on  A.OWNER||\'.\'||A.TABLE_NAME  = upper(tb.target_db||\'.\'||tb.source_table)  and  b.COLUMN_NAME = upper(tb.source_column)\r\n    \r\n    AND B.constraint_name=C.constraint_name AND C.CONSTRAINT_TYPE=\'P\'\r\n    --WHERE A.OWNER=\'XTGL\' AND A.TABLE_NAME=\'GY_TGDW\'\r\n    group by  A.OWNER,A.TABLE_NAME\r\n)\r\n,cte1 as(\r\n    SELECT \r\n      A.OWNER,A.TABLE_NAME,\r\n      listagg(\'par_\'||tb.source_column|| \'=before.\' || tb.source_column,\',\') within group (order by A.OWNER,A.TABLE_NAME ) val\r\n    FROM cte2 A\r\n    LEF